mapper.select:
SELECT o.`code` AS `code`, o.pay_price AS pay_price, o.pay_code as pay_code, o.`status` AS `status`, o.carbon_money, r.title, r.pic AS pic, r.image AS image, r.path, rl.`name` AS level_name, lp.addressFull AS address, o.report_code AS report_code, o.create_time AS create_time, o.update_time AS update_time FROM t_order AS o LEFT JOIN t_report AS r ON o.report_code = r.`code` LEFT JOIN t_landed_property AS lp ON r.houses_code = lp.`code` LEFT JOIN t_report_level AS rl ON r.level_code = rl.`code`
o.create_user = #{createUser,jdbcType=VARCHAR}
AND o.`status` = #{status,jdbcType=INTEGER}
AND o.`status`!=4
order by o.update_time desc limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

SELECT count(1) FROM t_order AS o LEFT JOIN t_report AS r ON o.report_code = r.`code` LEFT JOIN t_landed_property AS lp ON r.houses_code = lp.`code` LEFT JOIN t_report_level AS rl ON r.level_code = rl.`code`
o.create_user = #{createUser,jdbcType=VARCHAR}
AND o.`status` = #{status,jdbcType=INTEGER}

SELECT o.id as id, o.`code` AS `code`, IFNULL(p.`name`,'') AS payName, IFNULL(o.pay_price,'0.00') AS payPrice, u.phone AS phone, r.title AS reportTile, rl.`name` AS levelName, DATE_FORMAT(o.create_time, '%Y-%m-%d %H:%i:%s' ) AS createTime, o.`status` as status FROM t_order AS o LEFT JOIN t_pay_type AS p ON o.pay_code = p.`code` LEFT JOIN t_user AS u ON o.create_user = u.user_code LEFT JOIN t_report AS r ON o.report_code = r.`code` LEFT JOIN t_landed_property AS lp ON r.houses_code = lp.`code` LEFT JOIN t_report_level AS rl ON r.level_code = rl.`code`
1 = 1
and o.`status` = #{status,jdbcType=INTEGER}
and o.`code` = #{code , jdbcType=VARCHAR}
and u.phone = #{phone , jdbcType=VARCHAR}
and r.title = #{reportTitle , jdbcType=VARCHAR}
and rl.`code` = #{level , jdbcType=VARCHAR}
order by o.update_time desc
