mapper.select:
select o.code as orderCode, o.create_time as createTime, o.order_status as orderStatus, o.pay_money as payMoney, o.buyer_phone as mobile, d.product_code as productCode, d.buy_num as buyNums from t_product_order o left join t_product_order_detail d on o.code = d.order_code where o.buyer_code = #{buyerCode , jdbcType=VARCHAR}
and o.order_status = #{orderStatus , jdbcType=VARCHAR}
order by o.create_time desc limit #{pageIndex,jdbcType=INTEGER} , #{pageSize,jdbcType=INTEGER}
