mapper.select:
SELECT DATE_FORMAT(create_time,'%Y-%m-%d') as create_time, operation_type, ( SELECT `name` FROM sys_define_code WHERE `code` = operation_type ) AS operation_type_name, operation_type_child, ( SELECT `name` FROM sys_define_code WHERE `code` = operation_type_child ) AS operation_type_child_name, SUM(carbon_sum) as carbon_sum FROM t_user_carbon_operation WHERE user_code = #{userCode,jdbcType=VARCHAR}
AND operation_type =#{operationType,jdbcType=VARCHAR}
AND operation_type_child =#{operationTypeChild,jdbcType=VARCHAR}
AND create_time > DATE_SUB(CURDATE(), INTERVAL #{day,jdbcType=INTEGER} DAY) GROUP BY DATE_FORMAT(create_time, '%Y-%m-%d') ORDER BY create_time DESC
GROUP BY DATE_FORMAT(create_time, '%Y-%m-%d') ORDER BY create_time DESC limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

SELECT DATE_FORMAT( create_time, '%Y-%m-%d %H:%i:%s' ) AS create_time, operation_type, ( SELECT `name` FROM sys_define_code WHERE `code` = operation_type ) AS operation_type_name, operation_type_child, ( SELECT `name` FROM sys_define_code WHERE `code` = operation_type_child ) AS operation_type_child_name, carbon_sum FROM t_user_carbon_operation WHERE user_code = #{userCode,jdbcType=VARCHAR} and carbon_sum>0
AND operation_type =#{operationType,jdbcType=VARCHAR}
AND operation_type_child =#{operationTypeChild,jdbcType=VARCHAR}
AND create_time > DATE_SUB(CURDATE(), INTERVAL #{day,jdbcType=INTEGER} DAY) ORDER BY create_time DESC
ORDER BY create_time DESC limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
